var request = require('request');

module.exports.getUser = function(cookie) {
        request({
            url: 'http://85.30.249.228/backend/webapi/users',
            method: 'GET',
            json: true,
            headers: {
                'cookie': cookie
            }
        }, function(error, response, body) {
            if (!error) {
                console.log(body);
                return body.email;
            } else {
                return 'ERROR'
            }
        });
    };

